function PlayerDash1 () {
    isDashing = 1
    mySprite.ay = 0
    controller.moveSprite(mySprite, 0, 0)
    Spherecast1.follow(mySprite, 0)
    Spherecast2.follow(mySprite, 0)
    Spherecast3.follow(mySprite, 0)
    Spherecast4.follow(mySprite, 0)
    if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingLeft)) || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingLeft))) {
        Spherecast1.x += -56
        Spherecast2.x += -72
        Spherecast3.x += -56
        Spherecast4.x += -72
        Spherecast1.y += 8
        Spherecast2.y += 8
        Spherecast3.y += -8
        Spherecast4.y += -8
        if (tiles.tileAtLocationIsWall(Spherecast1.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast2.tilemapLocation()) || (tiles.tileAtLocationIsWall(Spherecast3.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast4.tilemapLocation()))) {
            mySprite.vy = 0
            mySprite.ay = 0
            mySprite.vx = -1000
        } else if ((tiles.tileAtLocationIsWall(Spherecast1.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast2.tilemapLocation()) || (tiles.tileAtLocationIsWall(Spherecast3.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast4.tilemapLocation()))) == false) {
            mySprite.vy = 0
            PlayerDash2()
        }
    } else if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingRight)) || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingRight))) {
        Spherecast1.x += 56
        Spherecast2.x += 72
        Spherecast3.x += 56
        Spherecast4.x += 72
        Spherecast1.y += 8
        Spherecast2.y += 8
        Spherecast3.y += -8
        Spherecast4.y += -8
        if (tiles.tileAtLocationIsWall(Spherecast1.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast2.tilemapLocation()) || (tiles.tileAtLocationIsWall(Spherecast3.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast4.tilemapLocation()))) {
            mySprite.vy = 0
            mySprite.ay = 0
            mySprite.vx = 1000
        } else if ((tiles.tileAtLocationIsWall(Spherecast1.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast2.tilemapLocation()) || (tiles.tileAtLocationIsWall(Spherecast3.tilemapLocation()) || tiles.tileAtLocationIsWall(Spherecast4.tilemapLocation()))) == false) {
            mySprite.vy = 0
            PlayerDash2()
        }
    }
    extraEffects.createSpreadEffectOnAnchor(mySprite, extraEffects.createSingleColorSpreadEffectData(9, ExtraEffectPresetShape.Spark), 200, 48, 20)
    music.play(music.createSoundEffect(WaveShape.Noise, 2175, 1, 255, 0, 200, SoundExpressionEffect.None, InterpolationCurve.Logarithmic), music.PlaybackMode.UntilDone)
    Spherecast1.follow(mySprite, 500)
    Spherecast2.follow(mySprite, 500)
    Spherecast3.follow(mySprite, 500)
    Spherecast4.follow(mySprite, 500)
    Spherecast1.setPosition(mySprite.x, mySprite.y)
    Spherecast2.setPosition(mySprite.x, mySprite.y)
    Spherecast3.setPosition(mySprite.x, mySprite.y)
    Spherecast4.setPosition(mySprite.x, mySprite.y)
    dashCounter += -1
    pause(50)
    mySprite.vy += -150
    mySprite.ay = 300
    controller.moveSprite(mySprite, 100, 0)
    isDashing = 0
}
