controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (OnMenu == 1) {
        ClearMenu()
        CreatePlayer()
        CreateUserInterface()
        if (maxUnlockedWeapons == 1) {
            CreateWeapon()
        }
        if (maxUnlockedWeapons == 3) {
            CreateWeapon()
            CreateRailcannonInterface()
        }
        if (currentLevel == 0) {
            introStorytelling()
            CreateLevel11()
        }
        if (currentLevel == 1) {
            CreateLevel12()
        }
        if (currentLevel == 2) {
            CreateLevel13()
        }
        if (currentLevel == 3) {
            CreateLevel14()
        }
        OnMenu = 0
    }
    if (OnEndScore == 0 && OnMenu == 0) {
        if (jumpCounter >= 1 && mySprite.isHittingTile(CollisionDirection.Bottom) == true) {
            mySprite.vy += -200
            jumpCounter += -1
        }
        if (mySprite.isHittingTile(CollisionDirection.Bottom) == false && (dashCounter > 0 && isDashing == 0)) {
            PlayerDash1()
        }
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.Portal, function (sprite, otherSprite) {
    EndLevel()
})
sprites.onOverlap(SpriteKind.soulEnemy, SpriteKind.RCProjectile, function (sprite, otherSprite) {
    sprites.destroy(otherSprite)
    extraEffects.createSpreadEffectOnAnchor(sprite, extraEffects.createSingleColorSpreadEffectData(9, ExtraEffectPresetShape.Spark), 200, 85)
    sprites.changeDataNumberBy(sprite, "health", -10)
    playerHP += 5
    pause(100)
    for (let value2 of spriteutils.getSpritesWithin(SpriteKind.soulEnemy, 64, sprite)) {
        music.play(music.createSoundEffect(WaveShape.Noise, 1154, 13, 255, 0, 150, SoundExpressionEffect.None, InterpolationCurve.Linear), music.PlaybackMode.InBackground)
        sprites.changeDataNumberBy(value2, "health", -5)
        extraEffects.createSpreadEffectOnAnchor(value2, extraEffects.createSingleColorSpreadEffectData(9, ExtraEffectPresetShape.Spark), 200, 48)
        if (sprites.readDataNumber(value2, "health") <= 0) {
            sprites.destroy(value2, effects.disintegrate, 100)
        }
    }
    if (sprites.readDataNumber(sprite, "health") <= 0) {
        sprites.destroy(sprite, effects.disintegrate, 100)
        playerKills += 1
    }
})
